// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract CryptoBond {
    address public owner;

    event BetResult(address indexed user, uint256 amount, bool won);

    constructor(){
        owner = msg.sender;
    }

    function bet() public payable {
    require(msg.value > 0, "Bet amount must be greater than zero");

    bool won = random() % 2 == 0;
    uint256 payout = won ? msg.value * 2 : msg.value / 2;

    // Transfer the payout amount based on the result
    payable(msg.sender).transfer(payout);

    emit BetResult(msg.sender, payout, won);
}


    function random() private view returns (uint256) {
    return uint256(keccak256(abi.encodePacked(block.prevrandao, block.timestamp, msg.sender)));}


}
